import './index.css';
import {useRef, useState} from 'react';
import {Canvas} from '@react-three/fiber'
import {OrbitControls, useGLTF} from '@react-three/drei';;

const content =[
  {tab: "Today"},
  {tab: "Wed 14"},
  {tab: "Thr 15"},
  {tab: "Fri 16"}
];

function App() {
    const ref = useRef()
    return (
        <div className="App">
            <div class="header">
                <div class="autoH">
                    <img class="logo" src={require("./logo.png")} alt="a"/>
                    <h1 class="weather">weather</h1>
                </div>
            </div>

            <div class="body">
                <img class="refresh" src={require("./refresh.png")} alt="b"/>
                <h2 class="temperature">12°C</h2>
                <h2 class="state">Partly Cloudy</h2>
                <h2 class="allDay">Day 14° • Night 4°</h2>
                {/* 3D model */}
                <div class="btnBg">
                    {content.map((section) => (<button>{section.tab}</button>))}
                </div>
            </div>

        </div>
    );
}

export default App;
