function App() {
    const ref = useRef()
    return (
        <div className="App">
            <div class="header">
                <div class="autoH">
                    <img class="logo" src={require("./logo.png")} alt="a"/>
                    <h1 class="weather">weather</h1>
                </div>
            </div>

            <div class="body">
            <img class="refresh" src={require("./refresh.png")} alt="b"/>
              <div class ="main">
                <h2 class="temperature">12°C</h2>
                <h3 class="state">Partly{" "}Cloudy</h3>
                <h4 class="allDay">Day 14° • Night 4°</h4>
                {/* 3D model */}
                </div>
                <button class="btn">Wed 14</button>

            </div>

        </div>
    );
}

export default App;
