import './index.css';
import React, {Suspense} from 'react';
import {Canvas} from '@react-three/fiber'
import {PerspectiveCamera, useGLTF, useRef, OrbitControls} from '@react-three/drei';
import {Environment} from '@react-three/drei'

function Model(props) {
    const {nodes} = useGLTF("/Untitled.gltf");
    return (
        <group {...props} dispose={null}>
            <PerspectiveCamera
                makeDefault={false}
                far={10000}
                near={10}
                fov={53.1}
                position={[-90, -90, 323.9]}
                rotation={[-0.1, 0, 0]}/>
            <mesh
                geometry={nodes.Polygon.geometry}
                material={nodes.Polygon.material}
                position={[-10, -17.4, -70]}/>
        </group>
    );
}

useGLTF.preload("/Untitled.gltf");

export default function App() {

    return (
        <div className="App">
            <div className="header">
                <div className="autoH">
                    <img className="logo" src={require("./logo.png")} alt="a"/>
                    <h1 className="weather">weather</h1>
                </div>
            </div>

            <div className="body">
                <img className="refresh" src={require("./refresh.png")} alt="b"/>
                <div className="main">
                    <h2 className="temperature">12°C</h2>
                    <h3 className="state">Partly{" "}Cloudy</h3>
                    <h4 className="allDay">Day 14° • Night 4°</h4>
                    <div className="product-canvas">
                        <Canvas size={[`1000px`,`2000px`]} style={{ background: '#272730', width:
                        `100%`, height: `500px`, position: `relative`, alignitems: `center`}}> <Suspense
                        fallback={null}> <Model scale ={[0.05,0.05,0.05]}/> <Environment preset="sunset"
                        background blur ={0.5} /> </Suspense> </Canvas> </div> <button
                        className="btn">Wed 14</button> </div> </div> </div>
    );
}
