import './index.css';
import {Suspense, useRef, useState} from 'react';
import {Canvas} from '@react-three/fiber'
import {OrbitControls, PerspectiveCamera, useGLTF} from '@react-three/drei';

export function Model(props) {
  const { nodes, materials } = useGLTF('./scene.gltf');
  return (
    <group {...props} dispose={null}>
      <mesh geometry={nodes.Group18985_default_0.geometry} material={materials['default']} rotation={[-Math.PI / 2, 0, 0]} />
      <mesh geometry={nodes.Box002_default_0.geometry} material={materials['default']} rotation={[-Math.PI / 2, 0, 0]} />
      <mesh geometry={nodes.Object001_default_0.geometry} material={materials['default']} rotation={[-Math.PI / 2, 0, 0]} />
    </group>
  )
}

function App() {
    const ref = useRef()
    return (
        <div className="App">
            <div className="header">
                <div className="autoH">
                    <img className="logo" src={require("./logo.png")} alt="a"/>
                    <h1 className="weather">weather</h1>
                </div>
            </div>

            <div className="body">
                <img className="refresh" src={require("./refresh.png")} alt="b"/>
                <div className="main">
                    <h2 className="temperature">12°C</h2>
                    <h3 className="state">Partly{" "}Cloudy</h3>
                    <h4 className="allDay">Day 14° • Night 4°</h4>
                    <div className ="product-canvas">
                    <Canvas>
                      <Suspense fallback = {null}>
                        <ambientLight />
                        <Model />
                      </Suspense>
                    </Canvas>
                    </div>
                    <button className="btn">Wed 14</button>
                </div>

            </div>

        </div>
    );
}

export default App;
